import Vue from 'vue'
import Router from 'vue-router'
Vue.use(Router)

function asyncGetRouter(name) {
	return (resolve) => require([`@/component/${name}.vue`], resolve)
}


export default new Router({
	mode: 'hash',
	routes: [{
			path: '/home',
			name: 'home',
			component: asyncGetRouter('home')
		},
		{
			path: '/about',
			name: 'about',
			component: asyncGetRouter('about')
		}
	]
})
